import React from "react";
import classes from "./AddTodo.module.css";
import Button from "../UI/Button";

const AddTodo = ({ addHandler }) => {
  const handleChange1 = event => {
    return `title: ${event.target.value}`;
  };

  const handleChange2 = event => {
    return `task: ${event.target.value}`;
  };

  const handleChange = event => {
    console.log(handleChange1, handleChange2);
  };

  return (
    <form onSubmit={addHandler} className={classes.input}>
      <div>
        <label htmlFor="title">Title</label>
        <input onChange={handleChange} type="text" id="title" name="title" />
      </div>
      <div>
        <label htmlFor="task">Task</label>
        <input onChange={handleChange} type="text" id="task" name="task" />
      </div>
      <Button type="submit">Add Task</Button>
    </form>
  );
};

export default AddTodo;
